from django.urls import pathfrom . import viewsapp_name = 'musicapp'urlpatterns = [	path('', views.index, name='index'),	path('(?P<album_id>[0-9]+)/', views.detail, name='detail'),	path('register/', views.register, name='register'),	path('login_user/', views.login_user, name='login_user'),	# path('profile/', views.user_profile, name='profile'),	path('logout_user/', views.logout_user, name='logout'),	path('create_album/', views.create_album, name='create_album'),	path('(?P<album_id>[0-9]+)/create_song/', views.create_song, name='create_song'),	# path('(?P<album_id>[0-9]+)/delete/', views.delete_album, name='delete-album'),]